using AssemblyLib.Utils;using System;using System.Collections.Generic;using System.Collections.ObjectModel;using System.Linq;using System.Text;using System.Threading.Tasks;namespace AssemblyLib.AssemblyModels{	public class BottomInputModel : Notifier	{		public BottomInputModel()		{					BottomSlope = "";			BottomThickness = "";			CAOfBottomPlate = "";			AnnularPlateReqd = "";			AnnularPlateThickness = "";		}				private string _BottomSlope;		public string BottomSlope			{				get { return _BottomSlope; }				set				{					_BottomSlope = value;					OnPropertyChanged(nameof(BottomSlope));				}			}				private string _BottomThickness;		public string BottomThickness			{				get { return _BottomThickness; }				set				{					_BottomThickness = value;					OnPropertyChanged(nameof(BottomThickness));				}			}				private string _CAOfBottomPlate;		public string CAOfBottomPlate			{				get { return _CAOfBottomPlate; }				set				{					_CAOfBottomPlate = value;					OnPropertyChanged(nameof(CAOfBottomPlate));				}			}				private string _AnnularPlateReqd;		public string AnnularPlateReqd			{				get { return _AnnularPlateReqd; }				set				{					_AnnularPlateReqd = value;					OnPropertyChanged(nameof(AnnularPlateReqd));				}			}				private string _AnnularPlateThickness;		public string AnnularPlateThickness			{				get { return _AnnularPlateThickness; }				set				{					_AnnularPlateThickness = value;					OnPropertyChanged(nameof(AnnularPlateThickness));				}			}			}}