using AssemblyLib.Utils;using System;using System.Collections.Generic;using System.Collections.ObjectModel;using System.Linq;using System.Text;using System.Threading.Tasks;namespace AssemblyLib.AssemblyModels{	public class BottomInputModel : Notifier	{		public BottomInputModel()		{					BottomSlope = "";			BottomThickness = "";			AnnularPlateReqd = "";			AnnularPlateWidth = "";			AnnularPlateThickness = "";			AnnularPlateType = "";		}				private string _BottomSlope;		public string BottomSlope			{				get { return _BottomSlope; }				set				{					_BottomSlope = value;					OnPropertyChanged(nameof(BottomSlope));				}			}				private string _BottomThickness;		public string BottomThickness			{				get { return _BottomThickness; }				set				{					_BottomThickness = value;					OnPropertyChanged(nameof(BottomThickness));				}			}				private string _AnnularPlateReqd;		public string AnnularPlateReqd			{				get { return _AnnularPlateReqd; }				set				{					_AnnularPlateReqd = value;					OnPropertyChanged(nameof(AnnularPlateReqd));				}			}				private string _AnnularPlateWidth;		public string AnnularPlateWidth			{				get { return _AnnularPlateWidth; }				set				{					_AnnularPlateWidth = value;					OnPropertyChanged(nameof(AnnularPlateWidth));				}			}				private string _AnnularPlateThickness;		public string AnnularPlateThickness			{				get { return _AnnularPlateThickness; }				set				{					_AnnularPlateThickness = value;					OnPropertyChanged(nameof(AnnularPlateThickness));				}			}				private string _AnnularPlateType;		public string AnnularPlateType			{				get { return _AnnularPlateType; }				set				{					_AnnularPlateType = value;					OnPropertyChanged(nameof(AnnularPlateType));				}			}			}}