using AssemblyLib.Utils;using System;using System.Collections.Generic;using System.Collections.ObjectModel;using System.Linq;using System.Text;using System.Threading.Tasks;namespace AssemblyLib.AssemblyModels{	public class DrawingListCRTInputModel : Notifier	{		public DrawingListCRTInputModel()		{					DocNo = "";			Description = "";		}				private string _DocNo;		public string DocNo			{				get { return _DocNo; }				set				{					_DocNo = value;					OnPropertyChanged(nameof(DocNo));				}			}				private string _Description;		public string Description			{				get { return _Description; }				set				{					_Description = value;					OnPropertyChanged(nameof(Description));				}			}			}}