using AssemblyLib.Utils;using System;using System.Collections.Generic;using System.Collections.ObjectModel;using System.Linq;using System.Text;using System.Threading.Tasks;namespace AssemblyLib.AssemblyModels{	public class RoofInputModel : Notifier	{		public RoofInputModel()		{					RoofSlopeOne = "";			RoofThickness = "";			CAOfRoof = "";			TopAngleType = "";			TopAngleSize = "";		}				private string _RoofSlopeOne;		public string RoofSlopeOne			{				get { return _RoofSlopeOne; }				set				{					_RoofSlopeOne = value;					OnPropertyChanged(nameof(RoofSlopeOne));				}			}				private string _RoofThickness;		public string RoofThickness			{				get { return _RoofThickness; }				set				{					_RoofThickness = value;					OnPropertyChanged(nameof(RoofThickness));				}			}				private string _CAOfRoof;		public string CAOfRoof			{				get { return _CAOfRoof; }				set				{					_CAOfRoof = value;					OnPropertyChanged(nameof(CAOfRoof));				}			}				private string _TopAngleType;		public string TopAngleType			{				get { return _TopAngleType; }				set				{					_TopAngleType = value;					OnPropertyChanged(nameof(TopAngleType));				}			}				private string _TopAngleSize;		public string TopAngleSize			{				get { return _TopAngleSize; }				set				{					_TopAngleSize = value;					OnPropertyChanged(nameof(TopAngleSize));				}			}			}}